SELECT COUNT(*) AS TTL_IN_PROC_TASK_CNT FROM ( SELECT DISTINCT ST.SVC_TASK_NUM FROM SVC_TASK ST ,SVC_TASK_STS STS WHERE ST.GLBL_CMPY_CD = #glblCmpyCd# AND ST.EZCANCELFLAG = '0' AND ST.SVC_TASK_STS_CD <> #pendChrg# AND ST.SVC_TASK_STS_CD <> #dbrfErr# AND UPPER(ST.SVC_TEAM_TXT) = UPPER(#svcTeamTxt#) AND STS.GLBL_CMPY_CD = ST.GLBL_CMPY_CD AND STS.EZCANCELFLAG = '0' AND STS.SVC_TASK_STS_CD = ST.SVC_TASK_STS_CD AND STS.OPEN_TASK_FLG = 'Y'
AND EXISTS ( SELECT 1 FROM DS_SVC_CALL_TP CALL WHERE CALL.GLBL_CMPY_CD = ST.GLBL_CMPY_CD AND CALL.EZCANCELFLAG = '0' AND CALL.DS_SVC_CALL_TP_CD = ST.DS_SVC_CALL_TP_CD AND CALL.ONS_SPRT_CALL_FLG = 'N' )
)

